import SwiftUIimport CoreDatastruct FavoritosView: View {    let userId: String    @State private var favoritos: [Favorito] = []    var body: some View {        VStack {            if favoritos.isEmpty {                Text("Nenhum Pok√©mon favoritado ainda.")                    .foregroundColor(.gray)                    .padding()            } else {                List(favoritos, id: \.self) { favorito in                    Text(favorito.nome ?? "Sem nome")                }            }        }        .navigationTitle("Seus Favoritos")        .onAppear {            carregarFavoritos()        }    }    private func carregarFavoritos() {        let context = PersistenceController.shared.container.viewContext        let request: NSFetchRequest<Favorito> = Favorito.fetchRequest()        request.predicate = NSPredicate(format: "userId == %@", userId)        do {            favoritos = try context.fetch(request)        } catch {            print("Erro ao carregar favoritos: \(error.localizedDescription)")        }    }}