import Foundationclass PokemonListViewModel: ObservableObject {    @Published var pokemons: [PokemonEntry] = []    private let api = Pokemon()    private var offset = 0    private let limit = 20    private var isLoading = false    func fetchPokemons() {        guard !isLoading else { return }        isLoading = true        api.getData(offset: offset, limit: limit) { [weak self] results in            DispatchQueue.main.async {                self?.pokemons.append(contentsOf: results)                self?.offset += self?.limit ?? 20                self?.isLoading = false            }        }    }}